from rest_framework import serializersfrom core.models.category import Categoryclass CategorySerializer(serializers.ModelSerializer):    children = serializers.CharField(source='get_children', read_only=True)    count_child = serializers.CharField(source='children_count', read_only=True)    class Meta:        model = Category        fields = (            'id', 'name', 'parent', 'children', 'count_child', 'created_at', 'updated_at'        )        # read_only_fields = ('children', 'count_child')## class ProductSerializer(serializers.ModelSerializer):#     class Meta:#         model = Product#         fields = '__all__'##     def to_representation(self, instance):#         response = super().to_representation(instance)#         response['category'] = CategorySerializer(instance.category).data#         return response