from core.models.article import Articlefrom core.serializers.article import ArticleSerializerfrom rest_framework.views import APIViewfrom rest_framework.response import Responsefrom rest_framework.status import HTTP_200_OK, HTTP_201_CREATED, HTTP_204_NO_CONTENTclass ArticleAPIView(APIView):    def get(self, request):        queryset = Article.objects.all()        serializer = ArticleSerializer(queryset, many=True)        return Response(serializer.data, status=HTTP_200_OK)    def post(self, request):        serializer = ArticleSerializer(data=request.data)        if serializer.is_valid(raise_exception=True):            serializer.save()            return Response(serializer.data, status=HTTP_201_CREATED)        else:            return Response(serializer.errors, status=HTTP_204_NO_CONTENT)